import React from 'react';
import { AppProvider } from './context/AppContext.jsx';
import MainRouter from './views/MainRouter.jsx'; // Sẽ tạo ở Bước 3

const App = () => {
  return (
    // Gói toàn bộ ứng dụng bằng Context Provider để truy cập trạng thái
    <AppProvider>
      <div className="min-h-screen bg-gray-100 font-sans">
        <MainRouter />
      </div>
    </AppProvider>
  );
};

export default App;
