// frontend/src/views/Dashboard.jsx

import React, { useState } from 'react';
import { useAppContext } from '../context/AppContext.jsx';
import { Cloud, Thermometer, Droplet, Zap, Gauge, RefreshCcw, XOctagon } from 'lucide-react'; 

// Component Card nhỏ cho từng Node
const NodeCard = ({ node, latestData, onControl }) => {
    const moistureData = latestData?.[node.id] || {};
    const moistureValue = moistureData.moisture !== undefined ? moistureData.moisture : '--';
    const tempValue = moistureData.temperature !== undefined ? moistureData.temperature : '--';
    
    // Logic hiển thị trạng thái
    const statusText = node.last_valve_status === 'OPEN' ? 'ĐANG TƯỚI' : 'ĐÃ ĐÓNG';
    const statusColor = node.last_valve_status === 'OPEN' ? 'bg-red-100 text-red-600' : 'bg-green-100 text-green-600';
    const statusIcon = node.last_valve_status === 'OPEN' ? XOctagon : Droplet;

    // Logic cảnh báo ngưỡng
    const isBelowThreshold = moistureValue !== '--' && moistureValue < node.threshold;
    const moistureColor = isBelowThreshold ? 'text-red-500' : 'text-emerald-600';

    return (
        <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-300 border border-gray-100 flex flex-col justify-between">
            <div>
                <div className="flex justify-between items-start mb-4">
                    <h3 className="text-xl font-bold text-gray-800">{node.name}</h3>
                    <div className={`px-3 py-1 text-xs font-semibold rounded-full ${statusColor} flex items-center`}>
                        <statusIcon className="w-4 h-4 mr-1" />
                        {statusText}
                    </div>
                </div>

                <p className="text-sm text-gray-500 mb-4">Vị trí: {node.location}</p>

                <div className="space-y-3 mb-6">
                    {/* Độ ẩm */}
                    <div className="flex items-center justify-between border-b pb-2">
                        <span className="flex items-center text-gray-700 font-medium">
                            <Cloud className="w-5 h-5 mr-2 text-blue-400" /> Độ Ẩm Hiện Tại:
                        </span>
                        <span className={`text-2xl font-extrabold ${moistureColor}`}>
                            {moistureValue}%
                        </span>
                    </div>

                    {/* Nhiệt độ */}
                    <div className="flex items-center justify-between border-b pb-2">
                        <span className="flex items-center text-gray-700 font-medium">
                            <Thermometer className="w-5 h-5 mr-2 text-orange-500" /> Nhiệt Độ:
                        </span>
                        <span className="text-xl font-bold text-orange-600">
                            {tempValue}°C
                        </span>
                    </div>

                    {/* Ngưỡng */}
                    <div className="flex items-center justify-between">
                         <span className="flex items-center text-gray-700 font-medium">
                            <Gauge className="w-5 h-5 mr-2 text-purple-500" /> Ngưỡng Min:
                        </span>
                        <span className="text-xl font-bold text-purple-600">
                            {node.threshold}%
                        </span>
                    </div>
                </div>
            </div>

            {/* Điều khiển thủ công */}
            <div className="flex flex-col space-y-2 pt-4 border-t border-gray-100">
                <button
                    onClick={() => onControl(node.id, 'MANUAL_OPEN')}
                    disabled={node.last_valve_status === 'OPEN'}
                    className="flex items-center justify-center w-full py-2 bg-emerald-600 text-white font-semibold rounded-lg hover:bg-emerald-700 transition disabled:bg-gray-400"
                >
                    <Droplet className="w-4 h-4 mr-2" /> Tưới Thủ Công
                </button>
                 <button
                    onClick={() => onControl(node.id, 'MANUAL_CLOSED')}
                    disabled={node.last_valve_status === 'CLOSED'}
                    className="flex items-center justify-center w-full py-2 bg-red-600 text-white font-semibold rounded-lg hover:bg-red-700 transition disabled:bg-gray-400"
                >
                    <XOctagon className="w-4 h-4 mr-2" /> Đóng Van
                </button>
            </div>
        </div>
    );
};

const Dashboard = () => {
    const { 
        sensorNodes, 
        latestSensorData, 
        isSocketConnected, 
        fetchAPI, 
        isLoading,
        setGlobalError // Cần để hiển thị lỗi điều khiển
    } = useAppContext();
    const [isControlling, setIsControlling] = useState(false);

    // Xử lý điều khiển thủ công
    const handleManualControl = async (nodeId, action) => {
        setIsControlling(true);
        setGlobalError(null);
        try {
            // Giả lập cuộc gọi API
            console.log(`Sending manual control: ${action} for ${nodeId}`);
            await fetchAPI(`control/valve/${nodeId}`, 'POST', { action });
            
            // Thông báo giả lập thành công
            alert(`Yêu cầu điều khiển Node ${nodeId} đã được gửi: ${action === 'MANUAL_OPEN' ? 'Mở Van' : 'Đóng Van'}`);

        } catch (e) {
            setGlobalError(`Lỗi điều khiển: ${e.message}`);
        } finally {
            setIsControlling(false);
        }
    };


    return (
        <div className="p-8">
            <header className="flex justify-between items-center border-b pb-4 mb-6">
                <h1 className="text-3xl font-extrabold text-gray-800">Bảng Điều Khiển Tổng Quan</h1>
                <div className={`px-4 py-2 rounded-full text-sm font-bold flex items-center shadow-md
                    ${isSocketConnected ? 'bg-green-600 text-white' : 'bg-red-600 text-white'}
                `}>
                    <RefreshCcw className={`w-4 h-4 mr-2 ${isSocketConnected ? 'animate-spin' : ''}`} />
                    {isSocketConnected ? 'Real-time (Đã kết nối)' : 'Ngắt kết nối'}
                </div>
            </header>

            {/* Hiển thị lỗi toàn cục */}
            {isLoading && (
                 <div className="p-4 bg-blue-100 border border-blue-300 text-blue-700 rounded-lg mb-6">
                    Đang tải cấu hình Node...
                </div>
            )}
            
            {sensorNodes.length === 0 && !isLoading && (
                 <div className="p-4 bg-yellow-100 border border-yellow-300 text-yellow-700 rounded-lg mb-6">
                    Chưa có Node cảm biến nào được cấu hình.
                </div>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {sensorNodes.map(node => (
                    <NodeCard 
                        key={node.id} 
                        node={node} 
                        latestData={latestSensorData}
                        onControl={handleManualControl}
                    />
                ))}
            </div>

            {/* Lịch sử tưới tiêu (Chưa triển khai) */}
            <div className="mt-8 pt-6 border-t border-gray-200">
                <h2 className="text-2xl font-bold text-gray-700 mb-4">Lịch Sử Hoạt Động Gần Đây</h2>
                <div className="p-4 bg-white rounded-xl shadow-md text-gray-500">
                    Chức năng hiển thị lịch sử tưới tiêu cần được triển khai.
                </div>
            </div>

        </div>
    );
};

export default Dashboard;
