import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom'; // Thư viện Routing mới
import { AppProvider } from './context/AppContext.jsx'; // Context của bạn
import App from './App.jsx'; // Component chính của ứng dụng
import './index.css'; // File CSS toàn cục (có thể chứa styling hoặc import Sass)

// --- Khởi tạo và Render Ứng dụng ---

// Tìm root element
const rootElement = document.getElementById('root');
if (!rootElement) {
    // Nên hiển thị lỗi nếu không tìm thấy root element
    console.error("Lỗi: Không tìm thấy element có id='root'.");
} else {
    // Tạo root và render
    const root = ReactDOM.createRoot(rootElement);

    root.render(
      <React.StrictMode>
        {/* BrowserRouter phải là wrapper ngoài cùng để các link hoạt động */}
        <BrowserRouter>
          {/* AppProvider cung cấp trạng thái Auth, API, Socket cho toàn ứng dụng */}
          <AppProvider>
            <App />
          </AppProvider>
        </BrowserRouter>
      </React.StrictMode>
    );
}
