// frontend/src/views/MainRouter.jsx

import React, { useState } from 'react';
import { useAppContext } from '../context/AppContext.jsx';
import LoginScreen from './LoginScreen.jsx'; 
import Dashboard from './Dashboard.jsx'; 
import ConfigurationScreen from './ConfigurationScreen.jsx'; 
import { Zap, Settings, User, LogOut } from 'lucide-react'; // Import icons

// Component bố cục chính
const MainLayout = () => {
    const { user, logout } = useAppContext();
    const [activeView, setActiveView] = useState('dashboard');

    const NavItem = ({ name, icon: Icon, view }) => (
        <button
            onClick={() => setActiveView(view)}
            className={`flex items-center space-x-3 p-3 rounded-xl transition duration-150 w-full text-left
                ${activeView === view ? 'bg-emerald-600 text-white shadow-lg' : 'text-gray-700 hover:bg-gray-100'}
            `}
        >
            <Icon className="w-5 h-5" /> 
            <span className="font-medium">{name}</span>
        </button>
    );

    const renderContent = () => {
        switch (activeView) {
            case 'dashboard':
                return <Dashboard />;
            case 'config':
                return <ConfigurationScreen />;
            default:
                return <Dashboard />;
        }
    };
    
    return (
        <div className="flex h-screen bg-gray-100 font-sans">
            {/* Sidebar */}
            <div className="w-64 bg-white shadow-xl flex flex-col justify-between p-4 transition-all duration-300">
                <div className="space-y-2">
                    <h1 className="text-2xl font-bold text-emerald-700 mb-6 flex items-center">
                        <Zap className="w-6 h-6 mr-2" />
                        Smart IR
                    </h1>
                    <NavItem name="Bảng Điều Khiển" icon={Zap} view="dashboard" />
                    <NavItem name="Cấu Hình Nodes" icon={Settings} view="config" />
                </div>
                
                <div className="space-y-2 pt-4 border-t border-gray-100">
                    <div className="flex items-center space-x-3 p-3 text-gray-700">
                        <User className="w-5 h-5" />
                        <span className="font-semibold">{user?.username || 'Guest'}</span>
                    </div>
                    <button
                        onClick={logout}
                        className="flex items-center space-x-3 p-3 rounded-xl transition duration-150 w-full text-left text-red-600 hover:bg-red-50"
                    >
                        <LogOut className="w-5 h-5" />
                        <span className="font-medium">Đăng Xuất</span>
                    </button>
                </div>
            </div>

            {/* Main Content */}
            <main className="flex-1 overflow-y-auto">
                {renderContent()}
            </main>
        </div>
    );
};


const MainRouter = () => {
    const { isAuthenticated, isLoading } = useAppContext();

    if (isLoading) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-gray-100">
                <div className="text-xl font-semibold text-emerald-600">Đang tải dữ liệu...</div>
            </div>
        );
    }

    return isAuthenticated ? <MainLayout /> : <LoginScreen />;
};

export default MainRouter;
