// --- Component Modal Thêm/Sửa Node (ĐÃ CẬP NHẬT) ---
const NodeModal = ({ isOpen, onClose, nodeToEdit, onSave }) => {
    const [name, setName] = useState('');
    const [location, setLocation] = useState('');
    const [threshold, setThreshold] = useState('');
    const [latitude, setLatitude] = useState(''); // MỚI
    const [longitude, setLongitude] = useState(''); // MỚI
    const [maxWatering, setMaxWatering] = useState(''); // MỚI
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (nodeToEdit) {
            // Chế độ Sửa
            setName(nodeToEdit.name);
            setLocation(nodeToEdit.location);
            setThreshold(nodeToEdit.threshold.toString());
            setLatitude(nodeToEdit.latitude ? nodeToEdit.latitude.toString() : ''); // Lấy dữ liệu mới
            setLongitude(nodeToEdit.longitude ? nodeToEdit.longitude.toString() : ''); // Lấy dữ liệu mới
            setMaxWatering(nodeToEdit.max_watering ? nodeToEdit.max_watering.toString() : ''); // Lấy dữ liệu mới
        } else {
            // Chế độ Thêm
            setName('');
            setLocation('');
            setThreshold('');
            setLatitude(''); // Khởi tạo
            setLongitude(''); // Khởi tạo
            setMaxWatering(''); // Khởi tạo
        }
        setError(null);
    }, [nodeToEdit, isOpen]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        
        const numericThreshold = parseInt(threshold, 10);
        const numericMaxWatering = parseInt(maxWatering, 10); // MỚI: Xử lý Ngưỡng Max
        const floatLat = parseFloat(latitude); // MỚI: Xử lý Lat/Lon
        const floatLon = parseFloat(longitude); // MỚI: Xử lý Lat/Lon

        // Logic kiểm tra dữ liệu
        if (isNaN(numericThreshold) || numericThreshold < 0 || numericThreshold > 100) {
            setError("Ngưỡng độ ẩm Tối thiểu phải là số từ 0 đến 100.");
            return;
        }
        if (isNaN(numericMaxWatering) || numericMaxWatering <= 0) {
            setError("Ngưỡng tưới Tối đa phải là một số dương (ví dụ: thời gian tưới tối đa tính bằng giây).");
            return;
        }
        if (isNaN(floatLat) || isNaN(floatLon)) {
             setError("Vĩ độ (Lat) và Kinh độ (Lon) phải là số hợp lệ.");
             return;
        }


        setIsSubmitting(true);
        // Gửi dữ liệu cập nhật
        const success = await onSave({
            id: nodeToEdit ? nodeToEdit.id : undefined,
            name,
            location,
            threshold: numericThreshold,
            latitude: floatLat, // DỮ LIỆU MỚI
            longitude: floatLon, // DỮ LIỆU MỚI
            max_watering: numericMaxWatering // DỮ LIỆU MỚI
        });
        
        setIsSubmitting(false);
        if (success) {
            onClose();
        }
    };

    if (!isOpen) return null;

    const isEditMode = !!nodeToEdit;

    // ... (Phần JSX giữ nguyên cấu trúc Modal)

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4" onClick={onClose}>
            <div 
                className="bg-white rounded-2xl p-8 w-full max-w-lg shadow-2xl transform transition-all duration-300"
                onClick={(e) => e.stopPropagation()} 
            >
                <div className="flex justify-between items-center border-b pb-4 mb-6">
                    <h2 className="text-2xl font-bold text-emerald-700">
                        {isEditMode ? `Sửa Node: ${nodeToEdit.id}` : 'Thêm Node Cảm Biến Mới'}
                    </h2>
                    <button onClick={onClose} className="text-gray-400 hover:text-gray-600 transition">
                        <X className="w-6 h-6" />
                    </button>
                </div>

                {error && (
                    <div className="p-3 bg-red-100 border border-red-300 text-red-700 rounded-lg mb-4 flex items-center">
                        <AlertTriangle className="w-5 h-5 mr-3" />
                        <span className="text-sm">{error}</span>
                    </div>
                )}

                <form onSubmit={handleSubmit} className="space-y-5">
                    {/* Tên Node */}
                    <div>
                        <label className="block text-sm font-semibold text-gray-700 mb-1 flex items-center">
                            <Tag className="w-4 h-4 mr-2 text-gray-500"/> Tên Node
                        </label>
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-emerald-500 focus:border-emerald-500 transition"
                            required
                        />
                    </div>
                    
                    {/* Vị Trí mô tả */}
                    <div>
                        <label className="block text-sm font-semibold text-gray-700 mb-1 flex items-center">
                            <MapPin className="w-4 h-4 mr-2 text-gray-500"/> Mô Tả Vị Trí
                        </label>
                        <input
                            type="text"
                            value={location}
                            onChange={(e) => setLocation(e.target.value)}
                            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-emerald-500 focus:border-emerald-500 transition"
                            required
                        />
                    </div>

                    {/* Vĩ độ & Kinh độ (MỚI) */}
                    <div className='grid grid-cols-2 gap-4'>
                        <div>
                            <label className="block text-sm font-semibold text-gray-700 mb-1 flex items-center">
                                <MapPin className="w-4 h-4 mr-2 text-gray-500"/> Vĩ Độ (Latitude)
                            </label>
                            <input
                                type="text"
                                value={latitude}
                                onChange={(e) => setLatitude(e.target.value)}
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-emerald-500 focus:border-emerald-500 transition"
                                placeholder="VD: 21.0285"
                                required
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-semibold text-gray-700 mb-1 flex items-center">
                                <MapPin className="w-4 h-4 mr-2 text-gray-500"/> Kinh Độ (Longitude)
                            </label>
                            <input
                                type="text"
                                value={longitude}
                                onChange={(e) => setLongitude(e.target.value)}
                                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-emerald-500 focus:border-emerald-500 transition"
                                placeholder="VD: 105.8542"
                                required
                            />
                        </div>
                    </div>

                    {/* Ngưỡng độ ẩm Tối thiểu */}
                    <div>
                        <label className="block text-sm font-semibold text-gray-700 mb-1 flex items-center">
                            <Gauge className="w-4 h-4 mr-2 text-gray-500"/> Ngưỡng Độ Ẩm Tối Thiểu (%)
                        </label>
                        <input
                            type="number"
                            value={threshold}
                            onChange={(e) => setThreshold(e.target.value)}
                            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-emerald-500 focus:border-emerald-500 transition"
                            min="0"
                            max="100"
                            required
                        />
                    </div>
                    
                    {/* Ngưỡng Tưới Tối Đa (MỚI) */}
                    <div>
                        <label className="block text-sm font-semibold text-gray-700 mb-1 flex items-center">
                            <Gauge className="w-4 h-4 mr-2 text-gray-500"/> Ngưỡng Tưới Tối Đa (Giây)
                        </label>
                        <input
                            type="number"
                            value={maxWatering}
                            onChange={(e) => setMaxWatering(e.target.value)}
                            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-emerald-500 focus:border-emerald-500 transition"
                            min="1"
                            required
                        />
                        <p className="text-xs text-gray-500 mt-1">Số giây tối đa hệ thống được phép tưới trong một lần (để bảo vệ thiết bị/cây trồng).</p>
                    </div>

                    <button
                        type="submit"
                        disabled={isSubmitting}
                        className="w-full flex justify-center py-2.5 px-4 rounded-lg shadow-md text-white font-bold bg-emerald-600 hover:bg-emerald-700 transition duration-200 disabled:opacity-50 items-center"
                    >
                        {isSubmitting ? (
                            <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                        ) : (
                            <Save className="w-5 h-5 mr-2" />
                        )}
                        {isEditMode ? 'Lưu Thay Đổi' : 'Thêm Node'}
                    </button>
                </form>
            </div>
        </div>
    );
};

// --- Component Chính: ConfigurationScreen ---
const ConfigurationScreen = () => {
    const { 
        sensorNodes, 
        isLoading, 
        fetchAPI, 
        fetchSensorNodes, 
        globalError, setGlobalError 
    } = useAppContext();

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [nodeToEdit, setNodeToEdit] = useState(null); // Node được chọn để sửa (null: chế độ Thêm)
    const [successMessage, setSuccessMessage] = useState(null);

    // Xóa thông báo sau một khoảng thời gian
    const clearMessages = () => {
        setGlobalError(null);
        setSuccessMessage(null);
    };
    
    // Xóa thông báo thành công sau 5s
    useEffect(() => {
        if (successMessage) {
            const timer = setTimeout(() => setSuccessMessage(null), 5000);
            return () => clearTimeout(timer);
        }
    }, [successMessage]);

    // Mở modal cho chế độ Thêm
    const handleAddNode = () => {
        setNodeToEdit(null);
        setIsModalOpen(true);
        clearMessages();
    };

    // Mở modal cho chế độ Sửa
    const handleEditNode = (node) => {
        setNodeToEdit(node);
        setIsModalOpen(true);
        clearMessages();
    };

    // Xử lý lưu (Thêm mới hoặc Cập nhật)
    const handleSaveNode = async (nodeData) => {
        const isEdit = !!nodeData.id;
        const endpoint = isEdit ? `config/nodes/${nodeData.id}` : 'config/nodes';
        const method = isEdit ? 'PUT' : 'POST';
        
        try {
            // Giả lập ID cho Node mới nếu là chế độ Thêm
            if (!isEdit) {
                const newId = `NODE${(sensorNodes.length + 1).toString().padStart(3, '0')}`;
                nodeData.id = newId; 
                nodeData.last_valve_status = 'CLOSED'; // Default status
            }

            // Gọi API (Giả lập)
            await fetchAPI(endpoint, method, nodeData); 
            
            setSuccessMessage(isEdit ? `Đã cập nhật cấu hình Node ${nodeData.id} thành công.` : `Đã thêm Node mới ${nodeData.id} thành công.`);
            
            // Tải lại danh sách Nodes từ Context sau khi thay đổi
            await fetchSensorNodes();
            return true; // Báo hiệu thành công

        } catch (e) {
            // Lỗi đã được set trong Context (globalError)
            return false; // Báo hiệu thất bại
        }
    };
    
    // Xử lý Xóa
    const handleDeleteNode = async (nodeId) => {
        // Sử dụng window.confirm để xác nhận, vì không được dùng alert/confirm mặc định
        if (!window.confirm(`Bạn có chắc chắn muốn xóa Node ${nodeId} không?`)) {
            return;
        }

        clearMessages();
        try {
            // Giả lập delete thành công
            await fetchAPI(`config/nodes/${nodeId}`, 'DELETE');
            setSuccessMessage(`Đã xóa Node ${nodeId} thành công.`);
            
            // Tải lại danh sách Nodes từ Context
            await fetchSensorNodes();
        } catch (e) {
            // Lỗi đã được set trong Context
        }
    };


    return (
        <div className="p-8">
            <NodeModal 
                isOpen={isModalOpen} 
                onClose={() => setIsModalOpen(false)} 
                nodeToEdit={nodeToEdit} 
                onSave={handleSaveNode}
            />

            <header className="flex justify-between items-center border-b pb-4 mb-6">
                <h1 className="text-3xl font-bold text-gray-800">Quản Lý Cấu Hình Node</h1>
                <button 
                    onClick={handleAddNode}
                    className="flex items-center px-4 py-2 bg-emerald-600 text-white font-semibold rounded-lg shadow-md hover:bg-emerald-700 transition"
                    disabled={isLoading}
                >
                    <PlusCircle className="w-5 h-5 mr-2" />
                    Thêm Node Mới
                </button>
            </header>

            {/* Hiển thị Thông báo */}
            {globalError && (
                 <div className="p-4 bg-red-100 border border-red-300 text-red-700 rounded-lg mb-4 flex items-center">
                    <AlertTriangle className="w-5 h-5 mr-3" />
                    <span className="font-semibold">Lỗi hệ thống:</span> {globalError}
                </div>
            )}
            {successMessage && (
                 <div className="p-4 bg-green-100 border border-green-300 text-green-700 rounded-lg mb-4 flex items-center">
                    <CheckCircle className="w-5 h-5 mr-3" />
                    <span className="font-semibold">Thành công:</span> {successMessage}
                </div>
            )}

            {isLoading ? (
                 <div className="p-8 text-center text-gray-500">
                    <Loader2 className="w-6 h-6 animate-spin mx-auto mb-2" />
                    Đang tải danh sách Nodes...
                </div>
            ) : (
                <div className="bg-white rounded-xl shadow-lg overflow-hidden">
                    <table className="min-w-full divide-y divide-gray-200">
                        <thead className="bg-gray-50">
                            <tr>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID Node</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Tên</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Vị Trí</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Ngưỡng Ẩm Min (%)</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Trạng thái Van</th>
                                <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">Thao Tác</th>
                            </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                            {sensorNodes.map((node) => (
                                <tr key={node.id} className="hover:bg-gray-50 transition duration-150">
                                    <td className="px-6 py-4 whitespace-nowrap text-sm font-bold text-gray-900">{node.id}</td>
                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{node.name}</td>
                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{node.location}</td>
                                    <td className="px-6 py-4 whitespace-nowrap text-sm text-purple-600 font-semibold">{node.threshold}</td>
                                    <td className="px-6 py-4 whitespace-nowrap">
                                        <span className={`px-3 py-1 text-xs font-semibold rounded-full ${node.last_valve_status === 'OPEN' ? 'bg-red-100 text-red-600' : 'bg-green-100 text-green-600'}`}>
                                            {node.last_valve_status}
                                        </span>
                                    </td>
                                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium flex justify-center space-x-2">
                                        <button 
                                            onClick={() => handleEditNode(node)}
                                            className="text-blue-600 hover:text-blue-800 p-2 rounded-full hover:bg-blue-50 transition"
                                            title="Chỉnh sửa"
                                        >
                                            <Edit className="w-5 h-5" />
                                        </button>
                                        <button 
                                            onClick={() => handleDeleteNode(node.id)}
                                            className="text-red-600 hover:text-red-800 p-2 rounded-full hover:bg-red-50 transition"
                                            title="Xóa"
                                        >
                                            <Trash2 className="w-5 h-5" />
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
            
            {/* Hiển thị khi không có Node nào */}
            {!isLoading && sensorNodes.length === 0 && (
                <div className="p-6 text-center text-gray-500 bg-white rounded-xl shadow-md">
                    Chưa có Node cảm biến nào được cấu hình. Hãy thêm một Node mới để bắt đầu.
                </div>
            )}
        </div>
    );
};

export default ConfigurationScreen;
