// frontend/src/views/LoginScreen.jsx

import React, { useState } from 'react';
import { useAppContext } from '../context/AppContext.jsx';
import { Droplet, AlertTriangle } from 'lucide-react';

const LoginScreen = () => {
    const { login, globalError, setGlobalError } = useAppContext();
    const [username, setUsername] = useState('admin');
    const [password, setPassword] = useState('admin123'); // Mật khẩu mặc định
    const [isSubmitting, setIsSubmitting] = useState(false);
    
    // Xóa lỗi khi người dùng bắt đầu nhập lại
    const handleInputChange = (setter, value) => {
        if (globalError) {
            setGlobalError(null);
        }
        setter(value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);
        // Hàm login sẽ tự động cập nhật isAuthenticated trong Context
        await login(username, password);
        setIsSubmitting(false);
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 p-4">
            <div className="w-full max-w-md bg-white p-8 rounded-2xl shadow-2xl transition-all duration-300 hover:shadow-3xl">
                <h2 className="text-3xl font-extrabold text-center text-emerald-600 mb-2 flex items-center justify-center">
                    <Droplet className="w-8 h-8 mr-3 text-emerald-500" />
                    Hệ Thống Tưới Thông Minh
                </h2>
                <p className="text-center text-gray-500 mb-8 font-medium">Đăng nhập để vào Bảng Điều Khiển</p>
                
                {/* Hiển thị lỗi toàn cục từ Context */}
                {globalError && (
                    <div className="flex items-center bg-red-50 border border-red-300 text-red-700 px-4 py-3 rounded-lg mb-6 shadow-sm">
                        <AlertTriangle className="w-5 h-5 mr-3 flex-shrink-0" />
                        <div>
                            <strong className="font-bold">Lỗi Đăng Nhập:</strong>
                            <span className="block text-sm"> {globalError}</span>
                        </div>
                    </div>
                )}
                
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label className="block text-sm font-semibold text-gray-700 mb-1" htmlFor="username">Tên đăng nhập</label>
                        <input
                            type="text"
                            id="username"
                            value={username}
                            onChange={(e) => handleInputChange(setUsername, e.target.value)}
                            className="w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:ring-emerald-500 focus:border-emerald-500 transition duration-150"
                            required
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-semibold text-gray-700 mb-1" htmlFor="password">Mật khẩu</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => handleInputChange(setPassword, e.target.value)}
                            className="w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:ring-emerald-500 focus:border-emerald-500 transition duration-150"
                            required
                        />
                    </div>
                    <button
                        type="submit"
                        disabled={isSubmitting}
                        className="w-full flex justify-center py-3 px-4 border border-transparent rounded-lg shadow-lg text-lg font-bold text-white bg-emerald-600 hover:bg-emerald-700 focus:outline-none focus:ring-4 focus:ring-offset-2 focus:ring-emerald-500 transition duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                        {isSubmitting ? 'Đang Đăng Nhập...' : 'Đăng Nhập'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default LoginScreen;
